import { Button, Col, Form, Image, Input, Row, Select, Space, Typography } from 'antd'
import { ArrowLeftOutlined } from '@ant-design/icons'
import { useRouter } from 'next/router'
import { createGift, editGift } from '@/firebase/db/gifts'
import { Gift, GiftFormType } from '@/types/gift'
import { FC, useEffect } from 'react'
import { useQuery } from 'react-query'
import { getUserLists } from '@/firebase/db/lists'
import { useUserContext } from '@/context/userContext'

interface Props {
    gift?: Gift
}

const { useForm, useWatch, Item } = Form
export const GiftForm: FC<Props> = ({ gift }) => {
    const [giftForm] = useForm()
    const imgUrl = useWatch('imgUrl', giftForm)

    const { back, query } = useRouter()
    const user = useUserContext()

    const { data: lists, isLoading } = useQuery('lists', getUserLists)

    const listsOptions = lists?.map(list => ({
        value: list.id,
        label: `${list.emoji} ${list.title}`,
    })) || []

    listsOptions.unshift({ label: 'Без списка', value: '' })

    useEffect(() => {
        if (!!gift) {
            giftForm.setFieldsValue(gift)
        }
    }, [gift])

    const createHandler = async (formData: GiftFormType) => {
        const data: GiftFormType = {
            name: formData.name,
            imgUrl: formData.imgUrl,
            //TODO undefined
            userId: user?.uid || '',
            createdTime: gift?.createdTime || Date.now(),
            ...Object.fromEntries(Object.entries(formData).filter(([key, value]) => !!value)),
        }

        if (!!query.giftId) {
            await editGift(query.giftId.toString(), data)
        } else {
            await createGift(data)
        }

        back()
    }

    return (
        <Space
            direction={'vertical'}
            style={{ width: '100%', backgroundColor: 'white', padding: 24, borderRadius: 24 }}
        >

            <Row align={'middle'} gutter={24} style={{ marginBottom: 16 }}>
                <Col><Button type={'text'} shape={'circle'} icon={<ArrowLeftOutlined/>} onClick={back}/></Col>
                <Col>
                    <Typography.Text
                        style={{
                            fontSize: 24,
                            fontWeight: 'bold',
                        }}
                    >
                        {gift ? 'Редактировать подарок' : 'Новый подарок'}
                    </Typography.Text>
                </Col>
            </Row>

            <Form form={giftForm} layout={'vertical'} onFinish={createHandler}>
                <Row style={{ width: '100%' }} gutter={24}>
                    <Col span={12}>
                        <Item name={'imgUrl'} label={'Ссылка на картинку'} required rules={[{ required: true }]}>
                            <Input autoComplete={'off'}/>
                        </Item>
                        <Image
                            width={'100%'}
                            src={imgUrl}
                            style={{ borderRadius: 12 }}
                            fallback='data:image/png;base64,'
                            preview={false}
                        />
                    </Col>
                    <Col span={12}>
                        <Item
                            name={'name'} label={'Название подарка'} required
                            rules={[{ required: true }, { min: 3 }]}
                        >
                            <Input autoComplete={'off'}/>
                        </Item>
                        <Item name={'price'} label={'Цена подарка'}>
                            <Input type={'number'} autoComplete={'off'}/>
                        </Item>
                        <Item name={'link'} label={'Ссылка на подарок'}>
                            <Input autoComplete={'off'}/>
                        </Item>
                        <Item name={'listId'} label={'Список подарков'} initialValue={gift?.listId || ''}>
                            <Select options={listsOptions}/>
                        </Item>
                        <Item>
                            <Button type={'primary'} htmlType={'submit'}>Сохранить</Button>
                        </Item>
                    </Col>
                </Row>
            </Form>
        </Space>
    )
}